/*TODO (Î™©ÏöîÏùº ÏôÑÎ£åÌï¥)
    1. ÌéòÏù¥ÏßÄ Ï†ÑÏ≤¥Ï†ÅÏù∏ ui
    2. Ïù¥ÎØ∏ÏßÄÎ•º ÎàåÎ†∏ÏùÑÎïå ÎùÑÏõåÏßÄÎäî Ï£ºÎ•òÏ†ïÎ≥¥Ï∞Ω
    3. Ï£ºÎ•òÏ†ïÎ≥¥ & ÌèâÏ†ê Î∞è Î¶¨Î∑∞ 
*/
import React from "react";
import S from "./styled";
import styled from "styled-components";
import { useNavigate, useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import Rating from "@mui/material/Rating";
import StarIcon from "@mui/icons-material/Star";
import Box from "@mui/material/Box";
import authClient from "../../apis/authClient";
import Container from "@mui/material/Container";
import { List, ListItem, ListItemText } from "@mui/material";
import axios from "axios";
import noAuthClient from "../../apis/noAuthClient";

const StyledList = styled(List)`
  display: flex;
  //flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  //justify-content: space-between;
  //border: 1px solid #ccc;
  padding: 10px;
  font-family: "BejuryuFont";
`;

const StyledListItem = styled.li`
  border-bottom: 1px solid #ccc;
  padding: 10px;
  flex: 1 0 48%;

  & span {
    font-family: "BejuryuFont";
  }
`;

const StyledImage = styled.img`
  width: 100px;
  height: 200px;
  object-fit: cover;
`;

function JuryuInfo() {
  const location = useLocation();
  const [value, setValue] = useState(2);
  const [hover, setHover] = useState(-1);
  // Ï£ºÎ•ò Î¶¨Î∑∞ ÌÖçÏä§Ìä∏ ÏûÖÎ†•Í∞í ÏÉÅÌÉú
  const [inputValue, setInputValue] = useState("");
  // Ïù¥ÎØ∏ÏßÄ
  const [decodedImage, setDecodedImage] = useState(null);
  const [drinkInfo, setDrinkInfo] = useState(null);
  // Ï£ºÎ•ò Î¶¨Î∑∞ Î¶¨Ïä§Ìä∏
  const [drinkReviewList, setDrinkReviewList] = useState([]);
  // Î¶¨Î∑∞ Î¶¨Ïä§Ìä∏ Í∏∏Ïù¥Ïóê Îî∞Îùº pagination
  // const [startIndex, setStartIndex] = useState(0); // ÏãúÏûë Ïù∏Îç±Ïä§ ÏÉÅÌÉú
  // const [endIndex, setEndIndex] = useState(4); // Ï¢ÖÎ£å Ïù∏Îç±Ïä§ ÏÉÅÌÉú
  // const [showMore, setShowMore] = useState(false); // ÎçîÎ≥¥Í∏∞ ÏÉÅÌÉú Ï∂îÍ∞Ä
  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(4);
  const totalPages = Math.ceil(drinkReviewList.length / perPage);

  const [alertMessage, setAlertMessage] = useState("");

  // ÌòÑÏû¨ ÌéòÏù¥ÏßÄÏóê Ìï¥ÎãπÌïòÎäî Ìï≠Î™©Îì§Îßå Ï∂îÏ∂úÌïòÎäî Ìï®Ïàò
  const getCurrentPageItems = () => {
    const startIndex = (page - 1) * perPage;
    const endIndex = startIndex + perPage;
    return drinkReviewList.slice(startIndex, endIndex);
  };

  // Îã§Ïùå ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò
  const goToNextPage = () => {
    if (page < totalPages) {
      setPage(page + 1);
    }
  };

  // Ïù¥Ï†Ñ ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò
  const goToPreviousPage = () => {
    if (page > 1) {
      setPage(page - 1);
    }
  };

  const juryuId = location.state?.juryuId;

  // Ïù¥ÎØ∏ÏßÄ ÎîîÏΩîÎî© Ìï®Ïàò
  const decodeBase64 = (base64) => {
    try {
      const binaryString = window.atob(base64);
      const bytes = new Uint8Array(binaryString.length);
      for (let i = 0; i < binaryString.length; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }
      return URL.createObjectURL(
        new Blob([bytes.buffer], { type: "image/png" })
      );
    } catch (error) {
      console.error(error);
      return null;
    }
  };

  // Ï£ºÎ•ò Ï†ïÎ≥¥ import
  useEffect(() => {
    const JuryuData = async () => {
      try {
        const response = await noAuthClient({
          method: "get",
          url: `drinks/${juryuId}`,
        });
        //const { name, image, dosu, price } = response.data;
        //const decodedImage = decodeBase64(image)'

        setDrinkInfo(response.data);

        if (response) {
          console.log(response.data);
        }
      } catch (error) {
        console.error(error);
      }
    };

    const juryuScore = async () => {
      try {
        const response = await noAuthClient({
          method: "get",
          url: `drinks/${juryuId}/rating`,
        });
        if (response) {
          console.log(response.data);
        }
      } catch (error) {
        console.log("juryuId error!", error.message);
      }
    };
    //console.log(drinkReviewList);

    const juryuReview = async () => {
      try {
        const response = await noAuthClient({
          method: "get",
          url: `drinks/${juryuId}/reviews`,
        });

        if (response) {
          setDrinkReviewList(response.data.reviews);
        }
      } catch (error) {
        console.error(error);
      }
    };

    JuryuData();
    juryuScore();
    juryuReview();
  }, [juryuId]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const navigate = useNavigate();

  const labels = {
    0.5: "ÌòïÌé∏ÏóÜÏñ¥",
    1: "ÎßõÏóÜÎäî Ï≤ô",
    1.5: "Í∑∏ÎÉ• Í∑∏Îûò",
    2: "Ï°∞Í∏à Í¥úÏ∞ÆÏùå",
    2.5: "ÎßõÏûàÎäî Ìé∏",
    3: "ÍΩ§ Í¥úÏ∞ÆÏùÑÏàòÎèÑ?",
    3.5: "Ï†ïÎßê ÎßõÏûàÏùå",
    4: "ÎßõÏûàÎäî ÏµúÍ≥†Î¥â",
    4.5: "Í∞êÎèôÏ†ÅÏù∏ Îßõ ü§§",
    5: "Í∞úÏ©êÎã§‚ù§Ô∏è",
  };

  const dictionary = () => {
    navigate("/dictionary");
  };

  const ranking = () => {
    navigate("/MyPage");
  };
  function getLabelText(value) {
    return `${value} Star${value !== 1 ? "s" : ""}, ${labels[value]}`;
  }

  // ÌòÑÏû¨ ÎÇ†ÏßúÏôÄ ÏãúÍ∞ÅÏùÑ "yyyy.MM.dd HH:mm" ÌòïÏãùÏúºÎ°ú Î∞òÌôòÌïòÎäî Ìï®Ïàò
  function getCurrentDateTime() {
    const date = new Date();

    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, "0");
    const day = String(date.getDate()).padStart(2, "0");
    const hours = String(date.getHours()).padStart(2, "0");
    const minutes = String(date.getMinutes()).padStart(2, "0");

    const formattedDateTime = `${year}-${month}-${day} ${hours}:${minutes}`;

    return formattedDateTime;
  }

  const currentDate = getCurrentDateTime(); // ÌòÑÏû¨ ÏãúÍ∞Å Í∞ÄÏ†∏Ïò§Í∏∞

  const handleReviewSubmit = async (e, juryuId) => {
    e.preventDefault();
    //alert("Î¶¨Î∑∞ Îì±Î°ùÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§!");
    //navigate("/dictionary");

    const labelKey = parseInt(value);

    try {
      const res = await authClient({
        method: "post",
        url: `drinks/${juryuId}/reviews`,
        data: {
          userId: localStorage.getItem("user-id"),
          comment: inputValue,
          score: labelKey * 2,
          date: currentDate,
        },
      });
      if (res) {
        console.log(res.data);
      }
    } catch (error) {
