2. [] Ï£ºÎ•ò Ï∂îÏ≤ú Í≤∞Í≥ºÎ•º whiteboxÎÇ¥Ïóê Íµ¨ÌòÑÌï† Í≤ÉÏù¥ÎØÄÎ°ú whiteboxÏûêÏ≤¥Î•º imageÎ°ú Ï≤òÎ¶¨ÌïòÏó¨ Í≤∞Í≥ºÍ≥µÏú†Í∞Ä Í∞ÄÎä•ÌïúÏßÄ, Ïï± ÏÑ§ÏπòÎßÅÌÅ¨?*/
import S from "./styled";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import logo from "../../image/bejuryu.png";
import authClient from "../../apis/authClient";
import noAuthClient from "../../apis/noAuthClient";
import { List, ListItem, ListItemText } from "@mui/material";

const StyledList = styled(List)`
  display: flex;
  //flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  //justify-content: space-between;
  //border: 1px solid #ccc;
  padding: 10px;
  font-family: "BejuryuFont";
`;

const StyledListItem = styled.li`
  border-bottom: 1px solid #ccc;
  padding: 10px;
  flex: 1 0 48%;

  & span {
    font-family: "BejuryuFont";
  }
`;

const StyledImage = styled.img`
  width: 100px;
  height: 200px;
  object-fit: cover;
`;

function Result() {
  const navigate = useNavigate();
  const location = useLocation();
  const data = location.state?.data;

  const [jwtToken, setJwtToken] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [drinkInfo, setDrinkInfo] = useState(null);
  // Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞ Ï†ÄÏû• ÏÉÅÌÉú
  const [resultData, setResultData] = useState(null);
  // drinkId ÏÉÅÌÉú
  const [drinkId, setDrinkId] = useState(null);

  // Í≤∞Í≥º Î∂àÎü¨Ïò§Í∏∞ ÏúÑÌïú Î°úÏßÅ
  useEffect(() => {
    const getSentiment = async (e, analysisId) => {
      try {
        const response = await authClient({
          method: "get",
          url: `/analyze/${analysisId}`,
        });
        setResultData(response.data.data);
        setDrinkId(response.data.data.drinkId);
        console.log(resultData);
      } catch (error) {
        const err = error.response.data;
        console.log(err);
      }
    };

    if (data) {
      getSentiment(data.id);
    }
  }, [data]);

  useEffect(() => {
    const getDrink = async () => {
      try {
        const response = await noAuthClient({
          method: "get",
          url: `/drinks/${drinkId}`,
        });
        setDrinkInfo(response.data);
      } catch (error) {
        console.error(error);
      }
    };
    getDrink();
  }, [drinkId]);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    navigate("/result");
  };

  const [inputValue, setInputValue] = useState("");

  // Í≤∞Í≥º Í≥µÏú†
  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleButtonClick = () => {
    navigate("/recommend");
  };

  // Ï£ºÎ•ò Ï∂îÏ≤úÍ≤∞Í≥º Î©òÌä∏

  const SAD_CONTENT =
    "ÌûòÎì§ ÌïòÎ£®ÏôÄ Ïä¨ÌîîÏùÑ ÎäêÎÅºÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å,\nÏà† Ìïú ÏûîÌïòÎ©∞, Ïä¨ÌîîÏù¥ ÏãúÎì§Ïñ¥Í∞Ñ ÎßàÏùåÏùÑ Îã§Ïãú ÌôúÍ∏∞Ï∞®Í≤å ÎßåÎì§Ïñ¥ Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?";
  const HAPPY_CONTENT =
    "ÏùºÏÉÅ ÏÜçÏóêÏÑú ÌñâÎ≥µÏùÑ ÎßåÎÅΩÌïòÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å, \nÏà† Ìïú ÏûîÌïòÎ©∞, ÏßÄÍ∏à Ïù¥ ÏàúÍ∞ÑÏóê Ï¶êÍ±∞ÏõÄÏùÑ ÎçîÌï¥Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?";
  const NEUTRAL_CONTENT =
    "ÏùºÏÉÅÏóêÏÑú Ï°∞ÌôîÏôÄ ÏïàÏ†ïÏùÑ ÎäêÎÅºÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å,\n Ïà† Ìïú ÏûîÌïòÎ©∞, Í∞ÄÎÅîÏùÄ ÌíÄÏñ¥ÎÇ¥Í≥† Ï¶êÍ±∞ÏõÄÏùÑ ÎçîÌï¥Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?";

  // resultData.sentiment;

  return (
    <S.Container>
      <S.Wrapper>
        <S.Title>ÌòÑÏû¨ Í∞êÏ†ïÏùÄ Í∏∞ÏÅ®!ÏûÖÎãàÎã§.</S.Title>
        <S.WhiteBox>
          <S.CenteredFormBox>
            <S.Title>üç∫Ï£ºÎ•òÏ†ïÎ≥¥üç∫</S.Title>
            <StyledImage src={logo} alt="Ï£ºÎ•ò Ïù¥ÎØ∏ÏßÄ" />
            <StyledList>
              <StyledListItem>
                <ListItemText primary={drinkInfo?.name} secondary="Ï£ºÎ•ò Ïù¥Î¶Ñ" />
              </StyledListItem>
              <StyledListItem>
                <ListItemText primary={drinkInfo?.dosu} secondary="ÎèÑÏàò" />
              </StyledListItem>
              <StyledListItem>
                <ListItemText primary={drinkInfo?.price} secondary="Í∞ÄÍ≤©" />
              </StyledListItem>
              <StyledListItem>
                <ListItemText primary={drinkInfo?.type} secondary="Ï¢ÖÎ•ò" />
              </StyledListItem>
            </StyledList>
          </S.CenteredFormBox>
          <div>{SAD_CONTENT}</div>
        </S.WhiteBox>

        <S.BtnList>
          <S.SubmitButton onClick={handleFormSubmit}>
            Í≤∞Í≥º Í≥µÏú†ÌïòÍ∏∞
          </S.SubmitButton>
          <S.SubmitButton onClick={handleButtonClick}>
            Îã§Ïãú Ï∂îÏ≤úÎ∞õÍ∏∞
          </S.SubmitButton>
        </S.BtnList>
      </S.Wrapper>
    </S.Container>
  );
}

export default Result;
