import S from "./styled";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import logo from "../../image/logo2.png";
import authClient from "../../apis/authClient";
import noAuthClient from "../../apis/noAuthClient";
import { List, ListItem, ListItemText } from "@mui/material";
import Modal from "@mui/material/Modal";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";

const { Kakao } = window;

const StyledList = styled(List)`
  display: flex;
  //flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  //justify-content: space-between;
  //border: 1px solid #ccc;
  padding: 10px;
  font-family: "BejuryuFont";
`;

const StyledListItem = styled.li`
  border-bottom: 1px solid #ccc;
  padding: 10px;
  flex: 1 0 48%;

  & span {
    font-family: "BejuryuFont";
  }
`;

const StyledImage = styled.img`
  width: 100px;
  height: 150px;
  object-fit: cover;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto;
`;

function Result() {
  const navigate = useNavigate();
  const location = useLocation();
  //const data = location.state?.data;
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const [jwtToken, setJwtToken] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [drinkInfo, setDrinkInfo] = useState(null);
  // Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞ Ï†ÄÏû• ÏÉÅÌÉú
  const [resultData, setResultData] = useState(null);
  // drinkId ÏÉÅÌÉú
  const [drinkId, setDrinkId] = useState(null);

  // Ïù¥ÎØ∏ÏßÄ ÎîîÏΩîÎî© Ìï®Ïàò
  const decodeBase64 = (base64) => {
    try {
      const binaryString = window.atob(base64);

      const bytes = new Uint8Array(binaryString.length);
      for (let i = 0; i < binaryString.length; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }

      return URL.createObjectURL(
        new Blob([bytes.buffer], { type: "image/png" })
      );
    } catch (error) {
      return null;
    }
  };

  // Ï£ºÎ•ò Ï∂îÏ≤ú Í≤∞Í≥º id
  const analysisId = location.state?.analysisId;

  // Ï£ºÎ•ò Ï∂îÏ≤úÍ≤∞Í≥º Î©òÌä∏
  const getSentence = (sentiment) => {
    switch (sentiment) {
      case "SAD_3":
        return {
          level: "Ïä¨Ìîî 3Îã®Í≥Ñüò•",
          comment:
            "ÌûòÎì§ ÌïòÎ£®ÏôÄ Ïä¨ÌîîÏùÑ ÎäêÎÅºÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å,\nÏà† Ìïú ÏûîÌïòÎ©∞, Ïä¨ÌîîÏù¥ ÏãúÎì§Ïñ¥Í∞Ñ ÎßàÏùåÏùÑ Îã§Ïãú ÌôúÍ∏∞Ï∞®Í≤å ÎßåÎì§Ïñ¥ Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      case "SAD_2":
        return {
          level: "Ïä¨Ìîî 2Îã®Í≥Ñüò•",
          comment:
            "ÌûòÎì§ ÌïòÎ£®ÏôÄ Ïä¨ÌîîÏùÑ ÎäêÎÅºÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å,\nÏà† Ìïú ÏûîÌïòÎ©∞, Ïä¨ÌîîÏù¥ ÏãúÎì§Ïñ¥Í∞Ñ ÎßàÏùåÏùÑ Îã§Ïãú ÌôúÍ∏∞Ï∞®Í≤å ÎßåÎì§Ïñ¥ Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      case "SAD_1":
        return {
          level: "Ïä¨Ìîî 1Îã®Í≥Ñüò•",
          comment:
            "ÌûòÎì§ ÌïòÎ£®ÏôÄ Ïä¨ÌîîÏùÑ ÎäêÎÅºÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å,\nÏà† Ìïú ÏûîÌïòÎ©∞, Ïä¨ÌîîÏù¥ ÏãúÎì§Ïñ¥Í∞Ñ ÎßàÏùåÏùÑ Îã§Ïãú ÌôúÍ∏∞Ï∞®Í≤å ÎßåÎì§Ïñ¥ Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      case "MEDIAN":
        return {
          level: "Ï§ëÎ¶Ωüëºüèª",
          comment:
            "ÏùºÏÉÅÏóêÏÑú Ï°∞ÌôîÏôÄ ÏïàÏ†ïÏùÑ ÎäêÎÅºÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å,\n Ïà† Ìïú ÏûîÌïòÎ©∞, Í∞ÄÎÅîÏùÄ ÌíÄÏñ¥ÎÇ¥Í≥† Ï¶êÍ±∞ÏõÄÏùÑ ÎçîÌï¥Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      case "HAPPY_1":
        return {
          level: "Í∏∞ÏÅ® 1Îã®Í≥ÑüòÅ",
          comment:
            "ÏùºÏÉÅ ÏÜçÏóêÏÑú ÌñâÎ≥µÏùÑ ÎßåÎÅΩÌïòÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å, \nÏà† Ìïú ÏûîÌïòÎ©∞, ÏßÄÍ∏à Ïù¥ ÏàúÍ∞ÑÏóê Ï¶êÍ±∞ÏõÄÏùÑ ÎçîÌï¥Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      case "HAPPY_2":
        return {
          level: "Í∏∞ÏÅ® 2Îã®Í≥ÑüòÅ",
          comment:
            "ÏùºÏÉÅ ÏÜçÏóêÏÑú ÌñâÎ≥µÏùÑ ÎßåÎÅΩÌïòÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å, \nÏà† Ìïú ÏûîÌïòÎ©∞, ÏßÄÍ∏à Ïù¥ ÏàúÍ∞ÑÏóê Ï¶êÍ±∞ÏõÄÏùÑ ÎçîÌï¥Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      case "HAPPY_3":
        return {
          level: "Í∏∞ÏÅ® 3Îã®Í≥ÑüòÅ",
          comment:
            "ÏùºÏÉÅ ÏÜçÏóêÏÑú ÌñâÎ≥µÏùÑ ÎßåÎÅΩÌïòÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å, \nÏà† Ìïú ÏûîÌïòÎ©∞, ÏßÄÍ∏à Ïù¥ ÏàúÍ∞ÑÏóê Ï¶êÍ±∞ÏõÄÏùÑ ÎçîÌï¥Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      default:
        return {
          level: "",
          comment: "",
        };
    }
  };

  // Ïπ¥Ïπ¥Ïò§ÌÜ° Î©îÏãúÏßÄ Ï†ÑÏÜ° Ìï®Ïàò
