import S from "./styled";
import styled from "styled-components";
import { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import logo from "../../image/bejuryu.png";
import authClient from "../../apis/authClient";
import noAuthClient from "../../apis/noAuthClient";
import { List, ListItem, ListItemText } from "@mui/material";
import Modal from "@mui/material/Modal";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";

const StyledTypography = styled(Typography)`
  font-family: "BejuryuFont";
`;

const StyledList = styled(List)`
  display: flex;
  //flex-direction: column;
  flex-wrap: wrap;
  align-items: center;
  //justify-content: space-between;
  //border: 1px solid #ccc;
  padding: 10px;
  font-family: "BejuryuFont";
`;

const StyledListItem = styled.li`
  border-bottom: 1px solid #ccc;
  padding: 10px;
  flex: 1 0 48%;

  & span {
    font-family: "BejuryuFont";
  }
`;

const StyledImage = styled.img`
  width: 100px;
  height: 150px;
  object-fit: cover;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto;
`;

function Result() {
  const navigate = useNavigate();
  const location = useLocation();
  //const data = location.state?.data;
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const [jwtToken, setJwtToken] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [drinkInfo, setDrinkInfo] = useState(null);
  // Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞ Ï†ÄÏû• ÏÉÅÌÉú
  const [resultData, setResultData] = useState(null);
  // drinkId ÏÉÅÌÉú
  const [drinkId, setDrinkId] = useState(null);

  // Ïù¥ÎØ∏ÏßÄ ÎîîÏΩîÎî© Ìï®Ïàò
  const decodeBase64 = (base64) => {
    const binaryString = window.atob(base64);
    const bytes = new Uint8Array(binaryString.length);
    for (let i = 0; i < binaryString.length; i++) {
      bytes[i] = binaryString.charCodeAt(i);
    }
    return URL.createObjectURL(new Blob([bytes.buffer], { type: "image/png" }));
  };
  // Ï£ºÎ•ò Ï∂îÏ≤ú Í≤∞Í≥º id
  const reviewId = location.state?.id;

  // idÏóê ÎßûÏ∂∞ Í≤∞Í≥º Î∂àÎü¨Ïò§Í∏∞

  // Ï£ºÎ•ò Ï∂îÏ≤úÍ≤∞Í≥º Î©òÌä∏
  const getSentence = (sentiment) => {
    switch (sentiment) {
      case "SAD_3":
      case "SAD_2":
      case "SAD_1":
        return {
          level: "Ïä¨Ìîîüò•",
          comment:
            "ÌûòÎì§ ÌïòÎ£®ÏôÄ Ïä¨ÌîîÏùÑ ÎäêÎÅºÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å,\nÏà† Ìïú ÏûîÌïòÎ©∞, Ïä¨ÌîîÏù¥ ÏãúÎì§Ïñ¥Í∞Ñ ÎßàÏùåÏùÑ Îã§Ïãú ÌôúÍ∏∞Ï∞®Í≤å ÎßåÎì§Ïñ¥ Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      case "MEDIAN":
        return {
          level: "Ï§ëÎ¶Ω",
          comment:
            "ÏùºÏÉÅÏóêÏÑú Ï°∞ÌôîÏôÄ ÏïàÏ†ïÏùÑ ÎäêÎÅºÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å,\n Ïà† Ìïú ÏûîÌïòÎ©∞, Í∞ÄÎÅîÏùÄ ÌíÄÏñ¥ÎÇ¥Í≥† Ï¶êÍ±∞ÏõÄÏùÑ ÎçîÌï¥Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      case "HAPPY_1":
      case "HAPPY_2":
      case "HAPPY_3":
        return {
          level: "üòÑÍ∏∞ÏÅ®üòÅ",
          comment:
            "ÏùºÏÉÅ ÏÜçÏóêÏÑú ÌñâÎ≥µÏùÑ ÎßåÎÅΩÌïòÍ≥† ÏûàÎäî ÎãπÏã†ÏóêÍ≤å, \nÏà† Ìïú ÏûîÌïòÎ©∞, ÏßÄÍ∏à Ïù¥ ÏàúÍ∞ÑÏóê Ï¶êÍ±∞ÏõÄÏùÑ ÎçîÌï¥Î≥¥Îäî Í≤ÉÏùÄ Ïñ¥Îñ®ÍπåÏöî?",
        };
      default:
        return {
          level: "",
          comment: "",
        };
    }
  };

  useEffect(() => {
    // Í≤∞Í≥º Î∂àÎü¨Ïò§Í∏∞ ÏúÑÌïú Î°úÏßÅ
    const getSentiment = async () => {
      try {
        const response = await authClient({
          method: "get",
          url: `/analyze/${reviewId}`,
        });
        console.log(response);
        const data = response.data;
        console.log(data);
        setResultData(data);

        if (response) {
          console.log(response.data);
        }
      } catch (error) {
        console.error(error);
      }
    };

    if (reviewId) {
      getSentiment();
    }
  }, [reviewId]);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    navigate("/result");
  };

  const [inputValue, setInputValue] = useState("");

  // Í≤∞Í≥º Í≥µÏú†
  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleButtonClick = () => {
    navigate("/recommend");
  };

  return (
    <S.Container>
      <S.Wrapper>
        <S.Title>ü§óÎ∂ÑÏÑù Í≤∞Í≥ºü§ó</S.Title>
        <S.BtnList>
          <S.WhiteBox>
            <S.Title>
              ÌòÑÏû¨ Í∞êÏ†ïÏùÄ {getSentence(resultData?.sentiment).level} ÏûÖÎãàÎã§!
            </S.Title>
            <StyledImage
              src={resultData && decodeBase64(resultData?.drinkImage)}
              alt="Ï£ºÎ•ò Ïù¥ÎØ∏ÏßÄ"
              style={{
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
              }}
            />
            <StyledList>
              <StyledListItem>
                <ListItemText
                  primary={resultData?.name}
                  secondary="Ï£ºÎ•ò Ïù¥Î¶Ñ"
                />
                <ListItemText
                  primary={`${resultData?.dosu}%`}
                  secondary="ÎèÑÏàò"
                />
                <ListItemText
                  primary={`${resultData?.price}Ïõê`}
                  secondary="Í∞ÄÍ≤©"
                />
              </StyledListItem>
              <StyledListItem>
                <ListItemText primary={resultData?.type} secondary="Ï¢ÖÎ•ò" />
                <ListItemText
                  primary={`${resultData?.sweetness}g`}
                  secondary="ÎãπÎèÑ(100mlÎãπ)"
                />
                <ListItemText
                  primary={resultData?.volume}
                  secondary="Ïö©Îüâ(ml)"
                />
              </StyledListItem>
            </StyledList>
            <S.Text>
              BeJuryuÏùò comment: {getSentence(resultData?.sentiment).comment}
            </S.Text>
          </S.WhiteBox>
          <S.WhiteBox2>
            <S.Title>ÏûÖÎ†•ÌïòÏÖ®Îçò Îç∞Ïù¥ÌÑ∞</S.Title>

            <S.ImageContainer>
              <S.Image
                src={decodeBase64(resultData?.facialExpression)}
              ></S.Image>
            </S.ImageContainer>
            <S.Text>
              <br />
              <br />
              {resultData.textExpression}
            </S.Text>
          </S.WhiteBox2>
        </S.BtnList>

        <S.BtnList>
          <S.SubmitButton onClick={handleInputChange}>
            Í≤∞Í≥º Í≥µÏú†ÌïòÍ∏∞
          </S.SubmitButton>
        </S.BtnList>
      </S.Wrapper>
    </S.Container>
  );
}

export default Result;
