import S from "./styled";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

function Recommend() {
  const navigate = useNavigate();
  const [jwtToken, setJwtToken] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("image", selectedFile);
    //formData.append("mood", inputValue);

    navigate("/result");
  };

  const RecommendView = () => {
    const [inputValue, setInputValue] = useState("");

    const handleInputChange = (e) => {
      setInputValue(e.target.value);
    };

    const handleFileChange = (e) => {
      if (e.target.files && e.target.files[0]) {
        const reader = new FileReader();
        reader.onload = (e) => {
          setImagePreview(e.target.result);
        };
        reader.readAsDataURL(e.target.files[0]);
        setSelectedFile(e.target.files[0]);
      }
    };

    return (
      <S.Container>
        <S.Wrapper>
          <S.Title>현재 기분을 표현해주세요!</S.Title>
          <S.WhiteBox>
            <S.textInput value={inputValue} onChange={handleInputChange} />
            {inputValue === "" && (
              <S.MyState>나의 기분을 입력해주세요..</S.MyState>
            )}
          </S.WhiteBox>

          <S.SubmitButton onClick={handleFormSubmit}>
            <strong>결과페이지로!</strong>
          </S.SubmitButton>
        </S.Wrapper>
      </S.Container>
    );
  };

  return <RecommendView />;
}

export default Recommend;
