import S from "./styled";
import { useState } from "react";
import { useNavigate } from "react-router-dom";

function Recommend() {
  const navigate = useNavigate();
  const [jwtToken, setJwtToken] = useState("");
  const [imagePreview, setImagePreview] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFormSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("image", selectedFile);
    //formData.append("mood", inputValue);

    navigate("/result");
  };

  const RecommendView = () => {
    const [inputValue, setInputValue] = useState("");

    const handleInputChange = (e) => {
      setInputValue(e.target.value);
    };

    const handleFileChange = (e) => {
      if (e.target.files && e.target.files[0]) {
        const reader = new FileReader();
        reader.onload = (e) => {
          setImagePreview(e.target.result);
        };
        reader.readAsDataURL(e.target.files[0]);
        setSelectedFile(e.target.files[0]);
      }
    };

    return (
      <S.Container>
        <S.Wrapper>
          <S.Title>ÌòÑÏû¨ Í∏∞Î∂ÑÏùÑ ÌëúÌòÑÌï¥Ï£ºÏÑ∏Ïöî!</S.Title>
          <S.WhiteBox>
            <S.FileInputWrapper>
              <S.FileInputLabel htmlFor="chooseFile">
                üëâ CLICK HERE! üëà
              </S.FileInputLabel>
              <S.FileInput
                type="file"
                id="chooseFile"
                name="chooseFile"
                accept="image/*"
                onChange={handleFileChange}
              />
            </S.FileInputWrapper>

            <S.textInput value={inputValue} onChange={handleInputChange} />
            {inputValue === "" && (
              <S.MyState>ÎÇòÏùò Í∏∞Î∂ÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî..</S.MyState>
            )}
          </S.WhiteBox>

          <S.SubmitButton onClick={handleFormSubmit}>
            <strong>Í≤∞Í≥ºÌéòÏù¥ÏßÄÎ°ú!</strong>
          </S.SubmitButton>
        </S.Wrapper>
      </S.Container>
    );
  };

  return <RecommendView />;
}

export default Recommend;
