/* TODO
1. [o] 주류 히스토리 페이지 대략적으로 어떻게 구현할 건지 UI*/

// 리스트 형식으로 들어가고 onclick 시키게끔 깔끔하게 고
import S from "./styled";
import { useNavigate, useLocaion } from "react-router-dom";
import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import authClient from "../../apis/authClient";

function History() {
  const navigate = useNavigate();

  // 추천결과 리스트
  const [recommendList, setRecommendList] = useState([]);
  // 리스트 길이에 따라 pagination
  const [page, setPage] = useState(1);
  const [perPage, setPerPage] = useState(4);
  const totalPages = Math.ceil(recommendList.length / perPage);

  // 현재 페이지에 해당하는 항목들만 추출하는 함수
  const getCurrentPageItems = () => {
    const startIndex = (page - 1) * perPage;
    const endIndex = startIndex + perPage;
    return recommendList.slice(startIndex, endIndex);
  };

  // 다음 페이지로 이동하는 함수
  const goToNextPage = () => {
    if (page < totalPages) {
      setPage(page + 1);
    }
  };

  // 이전 페이지로 이동하는 함수
  const goToPreviousPage = () => {
    if (page > 1) {
      setPage(page - 1);
    }
  };

  useEffect(() => {
    const reviewList = async () => {
      try {
        const response = await authClient({
          method: "get",
          url: "/analyze",
        });
        console.log(response);
        setRecommendList(response.data);
      } catch (error) {
        if (error.response) {
          const err = error.response.data;
          console.log(err);
        }
      }
    };
    reviewList();
  }, []);

  const goMypage = () => {
    navigate("/mypage");
  };
  const HistoryView = (
    <S.Container>
      <S.Wrapper>
        <S.Title>현재까지 추천받은 주류 목록입니다</S.Title>
        <S.WhiteBox>
          {getCurrentPageItems().map((data) => (
            <p key={data.id}></p>
          ))}
        </S.WhiteBox>
        <S.ReButton onClick={goMypage}>마이페이지로 돌아가기</S.ReButton>
      </S.Wrapper>
    </S.Container>
  );

  return HistoryView;
}

export default History;
