import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import S from "./styled";
import logo from "../../image/bejuryu.png";
import { connect } from "react-redux";

function Login() {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(false);

  // 카카오 로그인
  const loginWithKakao = () => {
    try {
      return new Promise((resolve, reject) => {
        if (!window.Kakao) {
          reject("Kakao 인스턴스가 존재하지 않습니다.");
        }
        window.Kakao.Auth.login({
          success: (auth) => {
            console.log("정상적으로 로그인 되었습니다.", auth);

            axios
              .post("/auth", { accessToken: auth.access_token }) // kakao 액세스 토큰값을 서버로 전송하는 POST 요청
              .then((response) => {
                console.log("서버로부터 응답을 받았습니다.", response.data);
              })
              .catch((error) => {
                console.error("서버로부터 응답을 받지 못했습니다.", error);
              });
            setIsLogin(true);
          },
          fail: (err) => {
            console.error(err);
          },
        });
      });
    } catch (err) {
      console.error(err);
    }
  };

  const logoutWithKakao = () => {
    if (window.Kakao.Auth.getAccessToken()) {
      console.log(
        "카카오 인증 액세스 토큰이 존재합니다.",
        window.Kakao.Auth.getAccessToken()
      );
      window.Kakao.Auth.logout(() => {
        console.log("로그아웃 되었습니다", window.Kakao.Auth.getAccessToken());
        setIsLogin(false);
      });
    }
  };

  useEffect(() => {
    const script = document.createElement("script");
    script.async = true;
    script.src = "https://developers.kakao.com/sdk/js/kakao.min.js";
    document.head.appendChild(script);
    script.onload = () => {
      window.Kakao.init("d19046b7f49f6376e5f701a2a57167e3");
      if (window.Kakao.Auth.getAccessToken()) {
        console.log("액세스 토큰이 존재합니다. 세션을 유지합니다.");
        setIsLogin(true);
      }
    };
  }, []);

  const loginView = (
    <S.Container>
      <S.Wrapper>
        <S.Title>BeJuRyu</S.Title>
        <S.Logo className="logo"></S.Logo>
        <S.Form>
          <img src={logo} alt="logo" width="50" height="50" />
        </S.Form>
        <S.BtnList>
          <div>
            <div
              id="kakao-login-btn"
              onClick={loginWithKakao}
              style={{ cursor: "pointer" }}
            >
              <img
                src="https://k.kakaocdn.net/14/dn/btroDszwNrM/I6efHub1SN5KCJqLm1Ovx1/o.jpg"
                width="222"
                alt="카카오 로그인 버튼"
              />
            </div>
          </div>
        </S.BtnList>
      </S.Wrapper>
    </S.Container>
  );

  const mainView = (
    <div>
      <p>메인 화면</p>
      <button onClick={logoutWithKakao}>카카오 로그아웃</button>
    </div>
  );

  return <div className="Login">{isLogin ? mainView : loginView}</div>;
}

export default Login;

